package com.liangxiaoqiao.leetcode.day.easy

import org.junit.jupiter.api.Test
import kotlin.test.assertEquals

class BullsAndCowsTest {

    private val target = BullsAndCows()

    @Test
    fun testBullAndCows() {
        assertEquals("1A3B", target.getHint("1807", "7810"))
        assertEquals("1A1B", target.getHint("1123", "0111"))
        assertEquals("95A859B", target.getHint("", ""))
    }
}